import React, { useEffect, useState } from 'react'
import './styles.css'
import Block from './Block/index'
import { getInfo } from '../../services/requests'

function Index() {
  const [activitiesData, setactivitiesData] = useState(null)

  useEffect(() => {
    getInfo().then(res => {
      setactivitiesData(res.data[0].nextActivities)
    })
  }, [])

  return (
    <div>
      <h1 className='tittleActivity'>Pr√≥ximas atividades</h1>
      <div className='stylesBlock'>
        {activitiesData ? (
          <>
            <Block
              course={activitiesData[0].course}
              upload={activitiesData.title}
              date={activitiesData.deadline}
              image={activitiesData.image}
            />
            <Block
              course='Projeto de Banco de Dados'
              upload='Enviar o esquema MySQL'
              date='10/10/20'
              image='https://i.imgur.com/RgQrlAS.jpg'
            />
          </>
        ) : (
          <p>Carregando</p>
        )}
      </div>
    </div>
  )
}

export default Index
